import React from 'react'
import { Add } from './components/Add'
import { News } from './components/News'
import './App.css'

class App extends React.Component {
  state = {
    news: null,
    isLoading: false,// статус для манипуляций "прелоадером" ("Загружаю..." в нашем случае)
  }

  componentDidMount() {
    this.setState({ isLoading: true })
    fetch('data/newsData.json')
      .then(response => {
        return response.json();
      })
      .then(data => {
        setTimeout(() => { // добавили задержку
          this.setState({ isLoading: false, news: data })
        }, 3000) // в три секунды
      })
  }

  handleAddNews = data => {
    const nextNews = [data, ...this.state.news]
    this.setState({ news: nextNews })
  }
  render() {
    const { news, isLoading } = this.state

    return (
      <React.Fragment>
        <Add onAddNews={this.handleAddNews} />
        <h3>Новости</h3>
        {isLoading && <p>Загружаю...</p> /*если isLoadin = false, то отобразить <p>*/ }  
        {Array.isArray(news) && <News data={news} /*если new = array, то отобразить news*//>}
      </React.Fragment>
    )
  }
}

export default App
